import React, { Component } from 'react';
import "bulma/bulma.sass";
import Sunny from "../ASSETS/sunny.png";
import cityName from "../Components/search.js";
import axios from 'axios';
function Icon(){
    
    let Data = sessionStorage.getItem('Data');
    let city = sessionStorage.getItem('city');
    Data = JSON.parse(Data);
    let temp = Math.round(Data.main.temp - 273.15);
    console.log(temp);
    let realFeel = Math.round(Data.main.feels_like - 273.15);
    console.log(realFeel);
    let iconQuery = `http://openweathermap.org/img/wn/${Data.weather[0].icon}@4x.png`
    console.log(iconQuery);
    
    
   
  
    return(
<div>    
    <div className="columns is-mobile mb-0 is-centered ">
        <div className="column is-centered is-offset-3 pt-0  pl-4">
            <figure className="image is-64x64 is-half is-centered is-offset-3 mt-0">
                <img src={iconQuery}></img>
            </figure>
        </div>
    </div>
    <div className="columns is-mobile is-multiline is-centered">
        <div className="column is-centered py-0">
            <h2 className="subtitle my-0">Sunny</h2><br/>
            <h1 className="title is-4">{city}</h1>

        </div>
    </div>
    <div className="columns is-mobile is-multiline is-centered mt-6 ">
        <div className="column is-centered py-0 is-multiline p-0">
                <h1 className="title is-size-1-mobile m-0 pb-0 mb-0">{temp}&#176;C</h1><br/>
                <h2 className="subtitle is-5 my-0">Real Feel {realFeel}&#176;C</h2><br/>

        </div>
    </div>
</div>
    )
}
export default Icon;
