import "bulma/bulma.sass";
import { render } from 'react-dom';
import React, { useEffect, useState } from "react";




function Search(){
    const [city, setCity] = useState("");
    
    function logg(){
        alert(city)
    }
    return(
    <div>
        
        <div className="field has-addons is-small">
            <div className="control is-small">
              <input className="input is-small" type="text" placeholder="City" value={city} onSubmit={(e) => setCity(e.target.value)}></input>
            </div>
        <div className="control">
            <input className="button is-black is-small is-info" type="submit" onClick={logg()}>
            
            </input>
        </div>
        </div>
        
    </div>    
    )


    
}
export default Search;
