import React, { useEffect, useState } from "react";
import "bulma/bulma.sass";
import Sunny from "../ASSETS/sunny.png";
import cityName from "../Components/search.js";
import axios from 'axios';
function Icon(){
    
    const [cityName, setCityName] = useState({
        city:"--",
    });
   const [cityData, setCityData] = useState

    
    
    function handleCityNameChange(event) {
        setCityName({ ...cityName, city: event.target.value });
    }
    const handleSubmit = (event) =>{
            event.preventDefault();
            console.log(cityName.city);
            axios({
                method: "GET",
                url: `http://api.openweathermap.org/data/2.5/weather?q=${cityName.city}&appid=4817328ee46eb2f4312147f65d825a38`
            })
            .then((response) => {
                sessionStorage.setItem('Data',JSON.stringify(response.data))
                // let Data = sessionStorage.getItem('Data');
                // Data = JSON.parse(Data);
                console.log(response.data.weather[0].icon)

                ;})

            sessionStorage.setItem('city',cityName.city)
                
                
        
    }
    
    
   
  
    return(
<div>
<form >
        <div className="field has-addons is-small">
            <div className="control is-small">
              <input className="input is-small" type="text" placeholder="City" name="city" onChange={handleCityNameChange} ></input>
            </div>
        <div className="control">
            <input className="button is-black is-small is-info" type="submit" onClick={() => {  handleSubmit();}}>
            
            </input>
        </div>
        </div>
    </form>    
    <div className="columns is-mobile mb-0 is-centered ">
        <div className="column is-centered is-offset-3 pt-5  pl-4">
            <figure className="image is-64x64 is-half is-centered is-offset-3 mt-0">
                <img src={Sunny}></img>
            </figure>
        </div>
    </div>
    <div className="columns is-mobile is-multiline is-centered">
        <div className="column is-centered py-0">
            <h2 className="subtitle my-0">Sunny</h2><br/>
            <h1 className="title is-4">London</h1>

        </div>
    </div>
    <div className="columns is-mobile is-multiline is-centered mt-6 ">
        <div className="column is-centered py-0 is-multiline p-0">
                <h1 className="title is-size-1-mobile m-0 pb-0 mb-0">20&#176;C</h1><br/>
                <h2 className="subtitle is-5 my-0">Real Feel 15&#176;C</h2><br/>

        </div>
    </div>
</div>
    )
}
export default Icon;
