import "bulma/bulma.sass";
import { render } from 'react-dom';
import React, { useEffect, useState } from "react";
import axios from 'axios';



class search extends React.Component{
    
     constructor(){
        super();
        this.forceUpdateHandler = this.forceUpdateHandler.bind(this);
        const [cityName, setCityName] = useState({
            city:"",
        });
      };

    
    
     handleCityNameChange(event) {
        setCityName({ ...cityName, city: event.target.value });
    }
     handleSubmit = (event) =>{
            event.preventDefault();
            console.log(cityName.city);
            axios({
                method: "GET",
                url: `http://api.openweathermap.org/data/2.5/weather?q=${cityName.city}&appid=4817328ee46eb2f4312147f65d825a38`
            })
            .then((response) => {
                sessionStorage.setItem('Data',JSON.stringify(response.data))
                // let Data = sessionStorage.getItem('Data');
                // Data = JSON.parse(Data);
                console.log(response.data.weather[0].icon)

                ;})

            sessionStorage.setItem('city',cityName.city)
                
                
        
    }
    forceUpdateHandler(){
        this.forceUpdate();
      };

  
    render(){
    <div>
    <form onSubmit={handleSubmit}>
        <div className="field has-addons is-small">
            <div className="control is-small">
              <input className="input is-small" type="text" placeholder="City" name="city"  onChange={handleCityNameChange}></input>
            </div>
        <div className="control">
            <input className="button is-black is-small is-info" type="submit" onClick={() => { forceUpdateHandler(); handleSubmit();}}>
            
            </input>
        </div>
        </div>
    </form>
    </div>    
    }


    
}
export default Search;
